mapper.select:
SELECT *
FROM user

WHERE

        user_sort_id = #{userSortId}


        AND company = #{company}


ORDER BY reporting_date DESC
LIMIT #{startIdx}, #{limit}

SELECT CEIL(COUNT(user_id)/#{limit}) AS count
FROM user

    WHERE

        user_sort_id = #{userSortId}


        AND company = #{company}

mapper.update:
UPDATE user
SET approval = now()
WHERE user_id = #{userId}
